mapper.insert:
insert into login_log (id, logintime, logouttime, 
  ip, loginuser, logindevice
  )
values (#{id,jdbcType=BIGINT}, #{logintime,jdbcType=TIMESTAMP}, #{logouttime,jdbcType=TIMESTAMP}, 
  #{ip,jdbcType=VARCHAR}, #{loginuser,jdbcType=VARCHAR}, #{logindevice,jdbcType=VARCHAR}
  )

mapper.select:
SELECT 
from login_log


    and id = #{id, jdbcType=BIGINT}


    and ip = #{ip,jdbcType=VARCHAR}


    and loginuser like #{loginuser,jdbcType=VARCHAR}



    and logindevice like #{logindevice,jdbcType=VARCHAR}



    and logintime >= #{logintimeBegin, jdbcType=VARCHAR}



    and logintime <= #{logintimeEnd, jdbcType=VARCHAR}



    and logouttime >= #{logouttimeBegin, jdbcType=VARCHAR}



    and logouttime <= #{logouttimeEnd, jdbcType=VARCHAR}
