mapper.select:
select 

,

from switch_syslog
where ID = #{id,jdbcType=BIGINT}
  and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}

mapper.delete:
delete from switch_syslog
where ID = #{id,jdbcType=BIGINT}
  and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}

mapper.insert:
insert into switch_syslog (ID, CREATE_TIME, IP_ADDRESS, 
  GENERATE_TIME, LOG_LEVEL, MSG
  )
values (#{id,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, #{ipAddress,jdbcType=VARCHAR}, 
  #{generateTime,jdbcType=TIMESTAMP}, #{logLevel,jdbcType=VARCHAR}, #{msg,jdbcType=LONGVARCHAR}
  )

mapper.update:
update switch_syslog
set IP_ADDRESS = #{ipAddress,jdbcType=VARCHAR},
  GENERATE_TIME = #{generateTime,jdbcType=TIMESTAMP},
  LOG_LEVEL = #{logLevel,jdbcType=VARCHAR},
  MSG = #{msg,jdbcType=LONGVARCHAR}
where ID = #{id,jdbcType=BIGINT}
  and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}

update switch_syslog
set IP_ADDRESS = #{ipAddress,jdbcType=VARCHAR},
  GENERATE_TIME = #{generateTime,jdbcType=TIMESTAMP},
  LOG_LEVEL = #{logLevel,jdbcType=VARCHAR}
where ID = #{id,jdbcType=BIGINT}
  and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}

mapper.select:
select
,

from switch_syslog


    and CREATE_TIME >= #{createTimeBegin,jdbcType=VARCHAR}


    and CREATE_TIME <= #{createTimeEnd,jdbcType=VARCHAR}


    and IP_ADDRESS = #{ipAddress,jdbcType=VARCHAR}


    and GENERATE_TIME >= #{generateTimeBegin,jdbcType=VARCHAR}


    and GENERATE_TIME <= #{generateTimeEnd,jdbcType=VARCHAR}


    and LOG_LEVEL = #{logLevel,jdbcType=VARCHAR}
