mapper.select:
	select 

  from sys_menus


  		parentid = #{id, jdbcType=BIGINT}


  		and menuname = #{menuname, jdbcType=VARCHAR}


  		and menuurl = #{menuurl, jdbcType=VARCHAR}


  		and parentid = #{parentid, jdbcType=BIGINT}


  		and menuicon = #{menuicon, jdbcType=VARCHAR}


  		and level = #{level, jdbcType=INTEGER}


  		and isbtn = #{isbtn, jdbcType=BIT}


  		and menuorder = #{menuorder, jdbcType=INTEGER}


  		and treeIconSkin = #{treeiconskin, jdbcType=BIT}


  		and isshow = #{isshow, jdbcType=BIT}


  ORDER BY menuorder asc

mapper.delete:
  	delete from sys_menus
  	 where id in

	    #{id}
